import React from 'react'
import um from "./Users.module.css";
import UserPhoto from "../assetss/images/userPhoto.jpg";
import {NavLink} from "react-router-dom";



let User = (props) => {
    return <div className={um.Main}>
                <div>
                    <NavLink to={'/Profile/' + props.user.id}>
                        <img className={um.img} src={props.user.photos.small != null ? props.user.photos.small : UserPhoto}/>
                    </NavLink>
                    <div>
                        {props.user.followed 
                            ? <button disabled={props.InProcess.some(id=> id === props.user.id)} onClick={() => {
                                props.OnUnFollow(props.user.id)
                            }} className={um.button}>
                                unfollow
                            </button>

                            : <button disabled={props.InProcess.some(id => id === props.user.id)} onClick={() => {
                                props.OnFollow(props.user.id)
                            }} className={um.button}>
                                follow
                            </button>}
                    </div>
                </div>
                <div className={um.Names}>
                    {props.user.name}
                    <div className={um.Posts}>
                        {props.user.status}
                    </div>
                </div>
                <div className={um.Country}>
                    {'country'}
                    {','}
                    <div className={um.City}>
                        {'city'}
                    </div>
                </div>



    </div>
}
export default User
