import React, {useState, useEffect} from "react"
import pc from './UserInfo.module.css'
import Preloader from "../../assetss/common/Loader/Loader";
import ProfileStatusF from "./ProfileStatusF";
import UserPhoto from "../../assetss/images/userPhoto.jpg";
import AboutMe from "./AboutMe"
import Contacts from "./Contacts"

const UserInfo = (props) => {

    if (!props.profile) {
        return <Preloader/>
    }

    const OnPhotoSelected = (e) => {
        if (e.target.files.length) {
            props.SetProfilePhoto(e.target.files[0])
        }
    }

    let [IsEdit, SetIsStatusChanging] = useState(false)
    let [Status, SetStatus] = useState(props.status)

    return <div className={pc.userdescrition}>

        <div className={pc.ProfileName}>
            {props.profile.fullName != null ? props.profile.fullName : null}
        </div>
        <div>
            <img id={"avatar"} className={pc.UserPhoto}
                 src={(props.profile.photos.large && props.profile.photos.small) == null ? UserPhoto : props.profile.photos.large}/>
        </div>
        <div>
            {props.IsMyPage &&
            <div>
                <input className={pc.SelectPhoto} type={"file"}
                       onChange={OnPhotoSelected}
                       accept="image/*"
                       id={"file"}/>
                <label htmlFor="file">Click for change avatar</label>
                <label htmlFor="file"><i className="material-icons"></i></label>
            </div>
            }
        </div>


        <ProfileStatusF status={props.status}
                        SetProfileStatus={props.SetProfileStatus}
                        IsMyPage={props.IsMyPage}/>

        <AboutMe aboutMe={props.profile.aboutMe}
                 lookingForAJob={props.profile.lookingForAJob}
                 lookingForAJobDescription={props.profile.lookingForAJobDescription}/>


        <Contacts contacts={props.profile.contacts}/>
    </div>
}
export default UserInfo;
