import React,{useState} from "react"

const DeleteMessage = (props) =>{
let [ToConfirmDeleting,SetToConfirmDeleting]=useState(false)
let [MessageDeleted,SetMessageDeleted]=useState(false)
let [MessageRestored,SetMessageRestored]=useState(false)

    return <div>
        {!MessageRestored && !MessageDeleted ?
            <button onClick={()=>{
                SetToConfirmDeleting(true)}
            }> delete this shit
            </button>
            : null
        }
        {ToConfirmDeleting &&
            <div>
                are you sure?
                <div>
                    <button onClick={() => {
                        SetMessageDeleted(true)
                        SetToConfirmDeleting(false)
                    }}>
                        yes
                    </button>
                    <button onClick={()=>{
                        SetToConfirmDeleting(false)}
                    }>
                        no
                    </button>
                </div>
            </div>
        }
        {MessageDeleted &&
            <button onClick={()=>{
                SetMessageRestored(true)
            }}>
                restore this shit
            </button>
        }

        </div>

}

export default DeleteMessage
