import React from 'react'
import um from "./Users.module.css";
import UserPhoto from "../assetss/images/userPhoto.jpg";
import {NavLink} from "react-router-dom";
import * as axios from "axios";


let PurifyUsers = (props) => {
    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize)
    let pages = []
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }
    return <div>
        {
            props.Users.map(us => <div key={us.id} className={um.Main}>
                <div>
                    <NavLink to={'/Profile/' +us.id}>
                        <img className={um.img} src={us.photos.small != null ? us.photos.small : UserPhoto}/>
                    </NavLink>
                    <div>
                        {us.followed
                            ? <button onClick={() => {
                                axios.get(`https://social-network.samuraijs.com/api/1.0/`)
                                    .then(responce => {
                                        this.props.ToggleIsFetching(false)
                                        this.props.setUsers(responce.data.items)
                                        this.props.SetTotalCountOfUsers(responce.data.totalCount)
                                    })
                                props.onUnFollow(us.id)

                            }} className={um.button}>
                                unfollow
                            </button>
                            : <button onClick={() => {
                                axios.get(`https://social-network.samuraijs.com/api/1.0/`)
                                    .then(responce => {

                                    })

                                props.onFollow(us.id)

                            }} className={um.button}>
                                follow
                            </button>}
                    </div>
                </div>
                <div className={um.Names}>
                    {us.name}
                    <div className={um.Posts}>
                        {us.status}
                    </div>
                </div>
                <div className={um.Country}>
                    {'country'}
                    {','}
                    <div className={um.City}>
                        {'city'}
                    </div>
                </div>
            </div>)
        }
        <div className={um.setNumbers}>
            {pages.map(p => {
                return (
                    <span className={props.currentPage === p && um.numbers}
                          onClick={(ev) => {
                              props.OnChangedPage(p)
                          }}>
                            {"    " + p}
                        </span>

                )
            })}
        </div>
    </div>
}
export default PurifyUsers
