import React from 'react'
import d from './Dialogs.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";



const Dialogs = (props) => {

    let dialogsElement = props.state.MessagesData.users.map(d => <DialogItem
        name={d.name}
        id={d.id}
        avatars={props.state.MessagesData.avatars}/>);
    let messagesElements = props.state.MessagesData.messages.map(m => < Message
        message={m.message}
        id={m.id}/>);


    let NewMessage = React.createRef();

    let SendMessage = () => {
        props.OnSendMessage()

    }


    let OnMessageChange = (e) => {
        let MessageText = e.target.value
        props.OnMessageChange(MessageText)
    }

    return (
        <div className={d.dialogs}>
            <div className={d.dialogsItems}>
                {dialogsElement}
            </div>
            <div className={d.messages}>

                {messagesElements}

                <div>
                    <div>
                  <textarea
                      placeholder='Enter Your Message'
                      ref={NewMessage}
                      value={props.state.MessagesData.NewMessageText}
                      onChange={OnMessageChange}>
                        </textarea>
                    </div>
                    <div>
                        <button onClick={SendMessage}> send message</button>
                    </div>
                </div>
            </div>

        </div>
    )
}
export default Dialogs;
