import {API} from "../API/requests";

const add_post = 'ADD-POST'
const type_post_info = 'TYPES-POST-INFO'
const Set_Profile_Of_User = 'Set_Profile_Of_User'

let DefaultState = {
        Posts: [
            {id: 1, Post: 'post1', likesCount: 12},
            {id: 2, Post: 'post2', likesCount: 2},
            {id: 3, Post: 'post3', likesCount: 11},
            {id: 4, Post: 'post4', likesCount: 5},
            {id: 5, Post: 'post5', likesCount: 121}
        ],
        TextNewPost: '',
    profile:null

    }

const ProfileReducer =  (state = DefaultState,action) => {
    switch (action.type) {
        case add_post: {
            let NewPost = {
                id: 5,
                Post: state.TextNewPost,
                likesCount: 0
            };
            return {
                ...state,
                Posts: [...state.Posts,{NewPost}],
                TextNewPost:''
            }
        }
        case type_post_info: {

            return {
                ...state,
                TextNewPost:action.newtext
            }
        }
        case Set_Profile_Of_User:{
            return {...state, profile : action.profile}
        }

        default :
            return state
    }
}
export const addpost_AC = () => {
    return {type : add_post }
}
export const type_post_info__AC = (text) => {
    return {type : type_post_info, newtext:text }
}
export const setProfileOfUser = (profile) => {
    return {type : Set_Profile_Of_User, profile }
}

export const GetProfileThunk = (ProfileID)=> {
    return (dispatch) =>{
        API.getProfile(ProfileID).then(data => {
            dispatch(setProfileOfUser(data))
        })
    }
}

export default ProfileReducer
