const SET_USER_DATA = 'SET_USER_DATA'

let DefaultState = {
    userId:null,
    email:null,
    login:null,
    IsLogined:false,
    IsFetching:false
}

const AuthReducer = (state = DefaultState, action) => {
    switch (action.type) {

      case SET_USER_DATA : {
          return {
                ...state,
                ...action.data,
              IsLogined:true
                }

        }
        default :
            return state

    }
}

export const SetAuthUser = (userId,email,login) =>
    ({type: SET_USER_DATA,data:{userId,email,login}})

export default AuthReducer
