import * as axios from "axios";

const instance = axios.create({
    withCredentials: true,
    baseURL: 'https://social-network.samuraijs.com/api/1.0/',
    headers: {
        "API-KEY": "1b0fde3a-3c72-4f1a-8a92-66508a392dc3"
    }
})

export const API = {
    async getUsers(currentPage = 1, pageSize = 10) {
        const response = await  instance.get(`users?page=${currentPage}&count=${pageSize}`)
                return response.data
    },

   async AuthMe() {
      const response = await instance.get(`auth/me`)
            return response.data
    },

   async getProfile(ProfileID = 2) {
       const response = await  instance.get(`profile/` + ProfileID)
                return response.data
    },
    async DelSub(userId) {
        const response = await  instance.delete(`follow/${userId}`)
            return response.data
    },

    async AddSub(userId) {
        const response = await instance.post(`follow/${userId}`)
            return response.data
    },
    async getProfileStatus(ProfileID = 2) {
        const response = await  instance.get(`profile/status/` + ProfileID)
                return response.data
    },
    async setProfileStatus(status) {
        const response = await instance.put(`profile/status/`,{status:status})
                return response.data

    },

async Login(email,password,rememberMe) {
    const response = await  instance.post(`auth/login/`,{email : email,
    password : password, rememberMe : rememberMe})
            return response.data
},
    async LogOut() {
        const response = await instance.delete(`auth/login`)
                return response.data

    }

}
