import React from 'react';
import pc from './MyPosts.module.css'
import Post from "./Post/Post";
import {Field, reduxForm} from "redux-form";

const PostForm = ()=>{
    return <div>

        <form onSubmit={props.handleSubmit} >
            <div>
                <Field name={'postArea'} placeholder={"type any info here"} component={"input"}/>

            </div>

            <div>
                <button> Enter </button>
            </div>
        </form>
    </div>
}

const MyPosts = (props) => {

    let Posts = props.posts.map(p => <Post message={p.Post} likecount={p.likesCount}/>);
    let NewPostEl = React.createRef();


    let onAddPost = () => {

        props.addpost()

    }

    let OnPostChange= () =>{

        let text = NewPostEl.current.value
        props.typePostInfo(text)

    }

    return (

        <div className={pc.pp}>
            <h3>My posts</h3>
            <div>
                <div>
                    <textarea value={props.TextNewPost} ref={NewPostEl}
                              onChange={OnPostChange} />
                </div>
                <div>
                    <button onClick={onAddPost}> add post</button>
                </div>
            </div>
            <div className={pc.marginPost}>
                {Posts }
            </div>
        </div>
    );
};
export default MyPosts;
