import React from 'react';
import Profile from "./Profile";
import {connect} from "react-redux";
import {withRouter} from 'react-router-dom'
import {setProfileOfUser} from '../../data/ProfileReduser'
import {getProfile} from "../../API/requests"


class ProfileContainer extends React.Component {
    componentDidMount() {

        let ProfileID = this.props.match.params.userID
        if(!ProfileID){
            ProfileID = 2
        }
        // это что-то типо конструктора, принято все вещи, которые нарушают идею чистых функцию писать тут

            API.getProfile(ProfileID).then(data => {
                this.props.setProfileOfUser(data)
                debugger
            })
    }

    render() {

        return (
            <Profile {...this.props} profile={this.props.profile}/>
        )
    }
}

// тут объект обернут скобками т.к мы хотим что бы функция вернула объект со свойством
let MapStateToProps = (state) => ({profile: state.ProfileData.profile})
/*тут короче понадобилось достучаться до линка и взять от туда айдику юзера
* что бы это сделать можно конечно пойти и взять всю строку урла, разбить её на части и тд. но есть
* свпособ проще, так же как и коннект существует метод WithRouter, он прокинет в пропсах нужную нам инфу*/
let WithRouterComponent = withRouter(ProfileContainer)
export default connect(MapStateToProps, {setProfileOfUser})(WithRouterComponent);
