import React, {useState, useEffect} from "react"
import ts from "./Tasks.module.css"
import classNames from 'classnames';
import DeleteButton from "./DeleteTaskButton";
import TaskDoneIcon from "./DoneIcone";
import EditTaskButton from "./EditTaskButton";
import TaskDetails from "./TaskDetails";


const Tasks = (props) => {

    let [TasksData, SetTasksData] = useState(props.ToDoListTasks.items)
    let [ShowButtonBlocked, SetShowButtonBlocked] = useState(false)

    const ShowButtonBlockedON = () => {
        SetShowButtonBlocked(true)
    }

    const ShowButtonBlockedOFF = () => {
        SetShowButtonBlocked(false)
    }

    useEffect(() => {
            SetTasksData(props.ToDoListTasks.items)
        }, [props.ToDoListTasks.items]
    )

    const TaskDonedStyleForIcon = classNames({
        '': true,
        'ss': false
    })
    const TaskDonedStyleForWindow = classNames({})
    //сделать стили, что бы при завершении таски она обводилась зеленым и икнока была обычной, как сейчас,
    // в случае, если не сделана - иконка дизейблед + начать добавлять снекбар

    return <div>
        {
            /*  props.ToDoListTasks.map(el => el.ListId === props.ListID && el.TaskData.items.map(key =>{
                  return <div key={key.id}>
                      {key.title}
                  </div>
                  })
              )*/
            props.ToDoListTasks.items.map(task => {
                return <div key={task.id} className={ts.Main}>

                    <div className={classNames(ts.MainWrapper)}>

                        <div className={ts.TaskEditButton}>
                            <EditTaskButton ButtonSize={"small"}/>
                        </div>

                        <div className={ts.Ramka}>
                            <div className={ts.TaskBodyWrapper}>
                                {task.completed ?
                                    <div className={ts.iconStatus}>
                                        <TaskDoneIcon/>
                                    </div>
                                    :
                                    <div>
                                    ?
                                    </div>
                                }


                                <div className={ts.Title}>
                                    {task.title}
                                </div>


                                <div className={ts.ToTrashIcon}>
                                    <DeleteButton DeleteTaskThunk={props.DeleteTaskThunk}
                                                  ListID={task.todoListId} TaskID={task.id}/>
                                </div>

                            </div>

                            <div>
                                <TaskDetails
                                    addedDate={task.addedDate}
                                    deadline={task.deadline}
                                    description={task.description}
                                    ShowButtonBlocked={ShowButtonBlocked}
                                    ShowButtonBlockedON={ShowButtonBlockedON}
                                    ShowButtonBlockedOFF={ShowButtonBlockedOFF}
                                />
                            </div>

                        </div>

                    </div>

                </div>

            })
        }
    </div>
}

export default Tasks
