import React from 'react';
import hc from './Header.module.css'
import {NavLink} from "react-router-dom";
import AccountIcon from '@material-ui/icons/AccountCircle';
import IconButtonHeader from '@material-ui/core/IconButton';
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";



const Header = (props) => {
    const renderMenu = (
        <Menu
            anchorEl={anchorEl}
            anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
            id={menuId}
            keepMounted
            transformOrigin={{ vertical: 'top', horizontal: 'right' }}
            open={isMenuOpen}
            onClose={handleMenuClose}
        >
            <MenuItem onClick={handleMenuClose}>Profile</MenuItem>
            <MenuItem onClick={handleMenuClose}> Logout </MenuItem>
        </Menu>
    );
    return <header className={hc.header}>
        <div className={hc.item}>
            {props.IsLogined
                ?
                <>
                    <div className={hc.AccountIconPosition}>
                    <IconButtonHeader
                        aria-label="account of current user"
                        aria-controls="primary-search-account-menu"
                        aria-haspopup="true"
                        color="primary"
                    >
                        <AccountIcon />
                    </IconButtonHeader>
                        {renderMenu}
                    </div>
                <div >{props.login}<div>
                    <button onClick={props.LogOutThunk} className={hc.log}>
                        Logout
                    </button>
                </div>
            </div>
                </>
                :
            <NavLink className={hc.log} to={'/login'} > Login </NavLink>}
        </div>
    </header>

}
export default Header;
