import {API} from "../API/requests";

const SUBMIT_USER_DATA = 'SUBMIT_USER_DATA'


let DefaultState = {
    email : "",
    password : "",
    rememberMe: false
}

const LoginReducer =  (state = DefaultState,action) => {
    switch (action.type) {

        case SUBMIT_USER_DATA : {
            return {
                ...state,
                ...action.data,
                IsLogined:true
            }

        }


        default :
            return state
    }
}

export const SubmitUserData = (email,password,rememberMe) =>
    ({type: SUBMIT_USER_DATA,data:{email,password,rememberMe}})



export  const SubmitUserDataThunk = (email,password,rememberMe) =>{
    return (dispatch)=>{
        API.SubmitDataLogin(email,password,rememberMe).then(data => {
            if (data.resultCode === 0){
                let {email,password,rememberMe} = data.data
                dispatch(SubmitUserData(email,password,rememberMe))

            }
        })
    }
}

export default LoginReducer
