const follow = 'FOLLOW'
const unfollow = 'UNFOLLOW'
const setusers = 'SET_USERS'
const SetCurrentPage= 'SET_CURRENT_PAGE'

let DefaultState = {
        Users: [],
        pageSize : 5,
        totalUsersCount:20,
        currentPage : 1
    }

const UsersReducer =  (state = DefaultState,action) => {
    switch (action.type) {
        case follow: {
        return {
          ...state,
          Users:state.Users.map(us => {
              if (us.id === action.userID) {
                  return {
                      ...us,
                      followed:true
                  }
              }
              return us
          })
        }
    }
        case unfollow: {
            return {
                ...state,
                Users: state.Users.map(us =>{
                    if(us.id === action.userID){
                        return {
                            ...us,
                            followed:false
                        }
                    }
                    return us
                })
            }
        }
        case setusers:{
            return {...state, Users:[...state.Users, ...action.users]}
        }
        case SetCurrentPage:{
            return {...state, currentPage: action.currentPage}
        }
        default :
            return state
    }
}
export const follow_AC = (userID) => {
    return {type : follow,userID:userID }
}
export const unfollow_AC = (userID) => {
    return {type : unfollow, userID:userID }
}
export const setUsers_AC = (users) => {
    return {type : setusers, users:users }
}
export const setCurrentPage_AC = (currentPage) => {
    return {type : SetCurrentPage, currentPage }
}
export default UsersReducer
