import React from 'react';
import Button from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';


const  TaskEditSaveButton = (props)=> {


const SaveNewToDoListTaskInfo= ()=>{
        props.ChangeToDoListTitleThunk(props.ListID,props.TaskID,props)
        props.EditTitleModeOFF()

}

    return (
        <div>
            <Button
                variant="contained"
                color="primary"
                onClick={SaveNewToDoListTaskInfo}
                size="small"
                startIcon={<SaveIcon />}
            >
                Save
            </Button>
        </div>
    );
}
export default TaskEditSaveButton
