})
    }

    StatusChangingOFF (){
        this.setState({
            StatusChanging: false
        })
    }
    render() {

        return (
            <div>
                {!this.StatusState.StatusChanging &&
                <div>
                         <span onClick={this.StatusChangingOFF.bind(this)}>

                       {this.props.status}
                        </span>

                </div>
                }
                {this.StatusState.StatusChanging &&
                <div>
                    <input value={this.props.status}/>
                </div>
                }


            </div>
        )
    }
}

