import React from 'react'
import * as axios from 'axios'
import PurifyUsers from '../Users/Users'
/* this component using for make requests on server for a new users and drawing
Ui component (Users)*/

class UsersAPItoServer extends React.Component {
    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page= ${this.props.currentPage}
    &count=${this.props.pageSize}`)
            .then(responce => {
                this.props.setUsers(responce.data.items)
                this.props.SetTotalCountOfUsers(responce.data.totalCount)
            })
    }

    OnChangedPage = (pageNumber) => {
        this.props.SetCurrentPage(pageNumber)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page= ${pageNumber}
    &count=${this.props.pageSize}`)
            .then(responce => {
                this.props.setUsers(responce.data.items)
            })
    }

    render() {

        return <PurifyUsers Users={this.props.Users}
                            totalUsersCount={this.props.totalUsersCount}
                            pageSize={this.props.pageSize}
                            OnChangedPage={this.OnChangedPage}
                            onFollow={this.props.onFollow}
                            onUnFollow={this.props.onUnFollow}/>
    }

}

export default UsersAPItoServer
