import React from "react"
import Preloader from "../../assetss/common/Loader/Loader";
import {NavLink} from "react-router-dom";
import mwuStyles from "./Messages.module.css"
import UserPhoto from "../../assetss/images/userPhoto.jpg";


const MessagesWithUser = (props) => {
    debugger
    return <div>
        {props.DialogsMessages ?
            props.DialogsMessages.items.map(item => {
                return (
                    <div key={item.id} className={mwuStyles.Main}>
                        <div className={mwuStyles.MessageFormStyle}>
                            <NavLink to={`/Profile/${item.senderId}`}>
                                {item.semderId !== props.MyID ?
                                    <img src={props.InterlocutorAvatar.small != null ?props.InterlocutorAvatar.small :UserPhoto}/>
                                    :
                                    <img src={props.UserAvatar.small != null ?props.UserAvatar.small :UserPhoto}/>
                                }
                            {item.senderName }
                            </NavLink>

                            <div>
                                &nbsp;&nbsp;  {item.body}
                            </div>
                        </div>

                        <div className={mwuStyles.dateStyle}>
                            <span>
                                &nbsp;
                                Sended at : &nbsp;&nbsp;
                                {item.addedAt.split("T")[0]}
                                &nbsp;  in &nbsp;
                                {item.addedAt.split("T")[1].split(".")[0]}
                            </span>
                        </div>

                    </div>

                )
            })
            : <Preloader/>
        }
    </div>
}
export default MessagesWithUser
