import React, {useState, useEffect} from "react"
import ts from "./Tasks.Module.css"


const Tasks = (props) => {
    let [TasksData, SetTasksData] = useState(props.ToDoListTasks.items)
    useEffect(() => {
            SetTasksData(props.ToDoListTasks.items)
        }, [props.ToDoListTasks.items]
    )

    return <div>
        {
            /*  props.ToDoListTasks.map(el => el.ListId === props.ListID && el.TaskData.items.map(key =>{
                  return <div key={key.id}>
                      {key.title}
                  </div>
                  })
              )*/
            props.ToDoListTasks.items.map(task => {
                return <div key={task.id} >
                    <div className={ts.TaskHeaderWrapper}>

                        <div>
                            start date
                        </div>

                        <div>
                            deadline
                        </div>
                    </div>

                    <div className={ts.TaskBodyWrapper}>

                        <div className={ts.www}>
                            icon status
                        </div>

                        <div>
                            {task.title}
                        </div>

                        <div>
                            icon to trash
                        </div>
                    </div>

                    <div className={ts.TaskFooterWrapper}>
                        <div>
                            description
                        </div>
                    </div>

                </div>
            })
        }
    </div>
}

export default Tasks
