    const MessageSendingModON = () => {
        SetChosedSendMessage(true)
    }

    const MessageSendingModOFF = () => {
        SetChosedSendMessage(false)
        props.SetProfileStatus(Status)

    }



    return (<div>
            {props.IsMyPage ?
                <div>
                    {!ChosedSendMessage ?
                        <div>
                <button onClick={MessageSendingModON}>
                Send message to {props.UserName}

                </button>
                        </div>
                        :
                        <div>
                            <input onChange={OnStatusChange} onBlur={StatusChangingOFF} autoFocus={true}
                                   value={Status}/>
                        </div>
                    }
                </div>
                : ""
            }
        </div>
    )
}


