import React, {useEffect,useState} from "react"
import Preloader from "../../assetss/common/Loader/Loader";
import {NavLink} from "react-router-dom";
import mwuStyles from "./Messages.module.css"
import UserPhoto from "../../assetss/images/userPhoto.jpg";


const MessagesWithUser = (props) => {

    let [UserAvatarUrl, SetUserAvatarUrl] = useState(props.MessagesData.UserAvatar)
    let [InterlocutorAvatarUrl, SetnterlocutorAvatarUrl] = useState(props.MessagesData.InterlocutorAvatar)

    useEffect(() => {
        SetUserAvatarUrl(props.MessagesData.UserAvatar)
        }, [props.MessagesData.UserAvatar]
    )
    useEffect(() => {
        SetnterlocutorAvatarUrl(props.MessagesData.InterlocutorAvatar)
        }, [props.MessagesData.InterlocutorAvatar]
    )

    debugger
    return <div>
        {props.MessagesData.DialogsMessages ?
            props.MessagesData.DialogsMessages.items.map(item => {
                return (
                    <div key={item.id} className={mwuStyles.Main}>
                        <div className={mwuStyles.MessageFormStyle}>
                            <div>
                                {item.senderName}
                            </div>
                            <NavLink to={`/Profile/${item.senderId}`}>
                                {item.senderId !== props.MyID ?
                                    <img className={mwuStyles.img}
                                         src={InterlocutorAvatarUrl != null
                                             ? InterlocutorAvatarUrl
                                             : UserPhoto}/>
                                    :
                                    <img className={mwuStyles.img}
                                         src={UserAvatarUrl != null ?
                                             UserAvatarUrl
                                             : UserPhoto}/>
                                }

                            </NavLink>

                            <div className={mwuStyles.messageText}>
                                &nbsp;&nbsp;  {item.body}
                            </div>
                        </div>

                        <div className={mwuStyles.dateStyle}>
                            <span>
                                &nbsp;
                                Sended at : &nbsp;&nbsp;
                                {item.addedAt.split("T")[0]}
                                &nbsp;  in &nbsp;
                                {item.addedAt.split("T")[1].split(".")[0]}
                            </span>
                        </div>

                    </div>

                )
            })
            : <Preloader/>
        }
    </div>
}
export default MessagesWithUser
