import React from 'react'
import um from "./Users.module.css";
import UserPhoto from "../assetss/images/userPhoto.jpg";
import {NavLink} from "react-router-dom";
import {DelUser} from "../../API/requests"


let PurifyUsers = (props) => {
    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize) // расчет кол-ва страниц
    let pages = []
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }
    return <div>
        {
            props.Users.map(us => <div key={us.id} className={um.Main}>
                <div>
                    <NavLink to={'/Profile/' + us.id}>
                        <img className={um.img} src={us.photos.small != null ? us.photos.small : UserPhoto}/>
                    </NavLink>
                    <div>
                        {us.followed
                            ? <button onClick={() => {
                                axios.delete(`https://social-network.samuraijs.com/api/1.0/follow/${us.id}`, {
                                    withCredentials: true,
                                    headers: {
                                        "API-KEY": "1b0fde3a-3c72-4f1a-8a92-66508a392dc3"
                                    }
                                })
                                    .then(responce => {
                                        if (responce.data.resultCode == 0) {
                                            props.onUnFollow(us.id)
                                        }
                                    })
                            }} className={um.button}>
                                unfollow
                            </button>

                            : <button onClick={() => {
                                axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${us.id}`, {}, {
                                    withCredentials: true,
                                    headers: {
                                        "API-KEY": "1b0fde3a-3c72-4f1a-8a92-66508a392dc3"
                                    }
                                })
                                    .then(responce => {
                                        if (responce.data.resultCode == 0) {
                                            props.onFollow(us.id)
                                        }
                                    })
                            }} className={um.button}>
                                follow
                            </button>}
                    </div>
                </div>
                <div className={um.Names}>
                    {us.name}
                    <div className={um.Posts}>
                        {us.status}
                    </div>
                </div>
                <div className={um.Country}>
                    {'country'}
                    {','}
                    <div className={um.City}>
                        {'city'}
                    </div>
                </div>
            </div>)
        }
        <div className={um.setNumbers}>
            {pages.map(p => {
                // нумерация страниц
                return (
                    <span className={props.currentPage === p && um.numbers}
                          onClick={(ev) => {
                              props.OnChangedPage(p)
                          }}>
                            {"    " + p}
                        </span>

                )
            })}
        </div>
    </div>
}
export default PurifyUsers
