import React from 'react';
import {Field, reduxForm} from "redux-form";
import {compose} from "redux";
import {connect} from "react-redux";
import {SubmitUserDataThunk} from "../../data/LoginReduser"
import {required,MaxLengthCreator} from "../utils/validators"
import {Input} from "../assetss/common/ValidatorsComponents/ElementsValidators"
import lm from "../Login/login.module.css"
import LoginUserPhoto from "../assetss/images/userLog.jpg"
import bg10 from "../assetss/images/bg10.jpg"

const maxLength50 = MaxLengthCreator(50)

const MapStateToProps =(state)=>{

}

let LoginForm = (props) => {


    return(

        <div >
            <span>
                <img src={bg10}/>
            </span>

        <form onSubmit={props.handleSubmit} className={lm.login} >

            <div >
                <img src={LoginUserPhoto} className={lm.imgUser}/>
                <Field  name={'login'} placeholder={"Enter the email"} component={Input}
                validate={[required,maxLength50]}/>

            </div>

            <div>
                <Field name={'password'} placeholder={"Enter the password"} component={Input}
                validate={[required,maxLength50]}/>

            </div>

            <div className={lm.rememberMe}>
                <Field name={'rememberMe'} component={"input"} type={"checkbox"}/> remember me

            </div>

            <div >
                <button className={lm.button} > Enter </button>

            </div>

        </form>
    </div>)
}
const ReduxLoginForm = reduxForm({form : 'login'}) (LoginForm)

let Login = (props) => {
   const  PostUserDataToServer = (formDatas) =>{
       console.log(formDatas)
props.SubmitUserDataThunk(formDatas.login,formDatas.password,formDatas.rememberMe)
       //here is all datas of forms, we can do here use some thunk for a make request on server
   }

    return( <div>

        <ReduxLoginForm onSubmit={PostUserDataToServer}  />
    </div>)
}

export default compose(
    connect(MapStateToProps, {SubmitUserDataThunk}),
    )(Login)
