import React,{useState,useEffect} from "react"
import {Input,CreateField,TextArea,TextAreaAboutMe} from "../../assetss/common/ValidatorsComponents/ElementsValidators";
import pc from './UserInfo.module.css'
import {MaxLengthCreator} from "../../utils/validators";

const ProfileDataEditorForm = (props) => {
    debugger
    let [fullName, SetfullName] = useState(props.fullName)
    let [lookingForAJob, SetlookingForAJob] = useState(props.lookingForAJob)
    let [lookingForAJobDescription, SetlookingForAJobDescription] = useState(props.lookingForAJobDescription)
    let [aboutMe, SetaboutMe] = useState(props.aboutMe)
    useEffect(() => {
        SetfullName(props.fullName)
        }, [props.fullName]
    )
    const OnfullNameChange = (e) => {
        SetfullName(e.currentTarget.value)
    }
    const OnlookingForAJobChange = (e) => {
        SetlookingForAJob(e.currentTarget.value)
    }
    const OnlookingForAJobDescriptionChange = (e) => {
        SetlookingForAJobDescription(e.currentTarget.value)
    }
    const OnaboutMeChange = (e) => {
        SetaboutMe(e.currentTarget.value)
    }

    return <div className={pc.editButton}>

        <form onSubmit={props.handleSubmit}>
            <button  >
                Save changes
            </button>
            <button className={pc.goback} onClick={props.ContactsEditingOFF} >
                go back
            </button>

            <div>
                <b>My name is : </b>
                {CreateField("Enter your full name","fullName",[],Input,
                    {value:fullName})}
            </div>

            <div>
                <b>are u looking for a job? :</b>
                {CreateField("","lookingForAJob",[],Input,{type : "checkbox",
                                         value:lookingForAJob})}
            </div>

            <div>
                <b>My professional skills:</b>
                {CreateField("Enter description","lookingForAJobDescription",[],
                    TextAreaAboutMe,{value:lookingForAJobDescription})}
            </div>
            <div>
                <b> about me:</b>
                {CreateField("print something about you","aboutMe",[],
                    TextAreaAboutMe,{value:aboutMe})}
            </div>


            {props.error
                ?
                <div className={pc.FormError}>
                    {props.error}
                </div>
                : null}

        </form>
    </div>
}

export default ProfileDataEditorForm
