import {API} from "../API/requests";

const add_post = 'ADD-POST'
const type_post_info = 'TYPES-POST-INFO'
const Set_Profile_Of_User = 'Set_Profile_Of_User'
const Set_Status_Of_User = 'Set_Status_Of_User'

let DefaultState = {
    Posts: [],
    TextNewPost: '',
    profile: null,
    status: "",
    email: "",
    password: "",
    rememberMe: false


}

const ProfileReducer = (state = DefaultState, action) => {
    switch (action.type) {
        case add_post: {
            let NewPost = {
                id: 6,
                Post: action.TextNewPost,
                likesCount: 0
            };
            return {
                ...state,
                Posts: [...state.Posts, NewPost],
            }
        }
        case type_post_info: {

            return {
                ...state,
                TextNewPost: action.newtext
            }
        }
        case Set_Profile_Of_User: {
            return {...state, profile: action.profile}
        }

        case Set_Status_Of_User: {
            return {...state, status: action.status}
        }

        default :
            return state
    }
}
export const addpost = () => {
    return {type: add_post}
}
export const typePostInfo = (text) => {
    return {type: type_post_info, newtext: text}
}
export const setProfileOfUser = (profile) => {
    return {type: Set_Profile_Of_User, profile}
}
export const SetStatusOfUser = (status) => {
    return {type: Set_Status_Of_User, status}
}

export const GetProfileThunk = (ProfileID) => {
    return (dispatch) => {
        API.getProfile(ProfileID).then(data => {
            dispatch(setProfileOfUser(data))
        })
    }
}
export const GetProfileStatusThunk = (userID) => {
    return (dispatch) => {
        API.getProfileStatus(userID).then(data => {
            dispatch(SetStatusOfUser(data))
        })
    }
}

export const SetProfileStatusThunk = (status) => {
    return (dispatch) => {
        API.setProfileStatus(status).then(data => {
            if (data.resultCode === 0) {
                dispatch(SetStatusOfUser(status))
            }
        })
    }
}

export default ProfileReducer
