const follow = 'FOLLOW'
const unfollow = 'UNFOLLOW'

let DefaultState = {
        Users: [
            {id: 1, name: 'Vlad',
                avalink:'https://mota.ru/upload/wallpapers/source/2016/07/10/22/05/49174/mota.ru_2016071020.jpg',
            followed:false,status:'hey im a 1',
            location:{city:'Moscow',country:'Russia'}},
            {id: 2, name: 'Maks',
                avalink:'https://mota.ru/upload/wallpapers/source/2016/07/10/22/05/49174/mota.ru_2016071020.jpg',
                followed:false,status:'hey im a 2',
                location:{city:'London',country:'Britania'}},
            {id: 3, name: 'Katya',
                avalink:'https://mota.ru/upload/wallpapers/source/2016/07/10/22/05/49174/mota.ru_2016071020.jpg',
                followed:false,status:'hey im a 3',
                location:{city:'Wancuwer',country:'Kanada'}},
            {id: 4, name: 'Yana',
                avalink:'https://mota.ru/upload/wallpapers/source/2016/07/10/22/05/49174/mota.ru_2016071020.jpg',
                followed:false,status:'hey im a 4',
                location:{city:'Kiev',country:'Ucraine'}},
        ]
    }

const UsersReducer =  (state = DefaultState,action) => {
    switch (action.type) {
        case follow: {
        return {
          ...state,
          Users:state.Users.map(us => {
              if (us.id === action.userID) {
                  return {
                      ...us,
                      followed:true
                  }
              }
              return us
          })
        }
    }
        case unfollow: {
            return {
                ...state,
                Users: state.Users.map(us =>{
                    if(us.id === action.userID){
                        return {
                            ...us,
                            followed:false
                        }
                    }
                    return us
                })
            }
        }
        case set_users:{
            return {...state, Users:[...state.Users,...action.users]}
        }
        default :
            return state
    }
}
export const follow_AC = (userID) => {
    return {type : follow }
}
export const unfollow_AC = (userID) => {
    return {type : unfollow, userID:userID }
}
export const setUsers_AC = (users) => {
    return {type : unfollow, users:users }
}
export default UsersReducer
